// src/components/CalendarChart.jsx
import React, { useMemo } from 'react';
import { ResponsiveParallelCoordinates } from '@nivo/parallel-coordinates';
import { useData } from '../context/DataProvider';

const CalendarChart = () => {
  const { totalCO2Data, loading } = useData();

  const calendarData = useMemo(() => {
    console.log(totalCO2Data)
    if (!totalCO2Data || !totalCO2Data.data || !totalCO2Data.meta) return [];
    const formattedData = totalCO2Data.data
      .map(item => ({
        value: item["Total (kkg CO2e)"],
        day: item["Year Built"] ? `${item["Year Built"]}-01-01` : undefined
      }))
      .filter(item => item.day !== undefined);

    const mergedData = formattedData.reduce((acc, { value, day }) => {
      acc[day] = (acc[day] || 0) + value;
      return acc;
    }, {});

    const result = Object.entries(mergedData).map(([day, value]) => ({ day, value }));

    return result;
  }, [totalCO2Data]);

  if (loading) return <div>Loading data...</div>;
  if (!calendarData.length) return <div>No data available</div>;

  console.log(calendarData)

  const testData = [
    {
      "value": 3.040225457197552,
      "day": 2022
    },
    {
      "value": 33.040225457197552,
      "day": 2023
    },
    {
      "value": 43.040225457197552,
      "day": 2010
    },
    {
      "value": 13.040225457197552,
      "day": 2016
    },
    {
      "value": 7.040225457197552,
      "day": 2018
    }
  ];

  return (
    <div style={{ height: '500px' }}>
      <ResponsiveParallelCoordinates
        data={testData}
        variables={[
            {
                id: 'day',
                label: 'temperature',
                value: 'temp',
                min: 'auto',
                max: 'auto',
                ticksPosition: 'before',
                legendPosition: 'start',
                legendOffset: 20
            },
            {
                id: 'value',
                value: 'cost',
                min: 0,
                max: 'auto',
                ticksPosition: 'before',
                legendPosition: 'start',
                legendOffset: 20
            }
        ]}
        margin={{ top: 50, right: 120, bottom: 50, left: 60 }}
        lineWidth={3}
        legends={[
            {
                anchor: 'right',
                direction: 'column',
                justify: false,
                translateX: 100,
                translateY: 0,
                itemsSpacing: 2,
                itemWidth: 60,
                itemHeight: 20,
                itemDirection: 'left-to-right',
                itemOpacity: 0.85,
                symbolSize: 20,
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemOpacity: 1
                        }
                    }
                ]
            }
        ]}
    />
    </div>
  );
};

export default CalendarChart;
