import React, { useMemo } from 'react';
import { ResponsiveLine } from '@nivo/line';
import { useData } from '../context/DataProvider';

const LineChart = () => {
  const { totalCO2Data, loading } = useData();

  const calendarData = useMemo(() => {
    console.log(totalCO2Data);
    if (!totalCO2Data || !totalCO2Data.data || !totalCO2Data.meta) return null;

    function getRandomColorHSL() {
      return `hsl(${Math.floor(Math.random() * 360)}, 70%, 50%)`;
    }

    const groupMap = {};

    totalCO2Data.data.forEach(item => {
      const type = item["UMD Type"];
      const name = item["Building Name"];
      const value = item["Total (kkg CO2e)"];

      if (!type || !name || typeof value !== "number") return;

      if (!groupMap[type]) {
        groupMap[type] = {
          name: type,
          color: getRandomColorHSL(),
          children: [],
        };
      }

      groupMap[type].children.push({
        name: name,
        color: getRandomColorHSL(),
        loc: value,
      });
    });

    const sunburstData = {
      name: "UMD Buildings",
      color: getRandomColorHSL(),
      children: Object.values(groupMap),
    };

    console.log(sunburstData);
    return sunburstData;
  }, [totalCO2Data]);

  if (loading) return <div>Loading data...</div>;
  if (!calendarData) return <div>No data available</div>;  

  console.log(calendarData)

  const data = [
    {
      id: 'Series 1',
      data: [
        { x: 'Jan', y: 200 },
        { x: 'Feb', y: 300 },
        { x: 'Mar', y: 400 },
        { x: 'Apr', y: 500 },
        { x: 'May', y: 600 },
      ],
    },
    {
      id: 'Series 2',
      data: [
        { x: 'Jan', y: 100 },
        { x: 'Feb', y: 400 },
        { x: 'Mar', y: 250 },
        { x: 'Apr', y: 450 },
        { x: 'May', y: 300 },
      ],
    },
  ];

  return (
    <div style={{ height: 400 }}>
    <ResponsiveLine
      data={data}
      margin={{ top: 50, right: 110, bottom: 50, left: 60 }}
      xScale={{ type: 'point' }}
      yScale={{
        type: 'linear',
        min: 'auto',
        max: 'auto',
        stacked: true,
        reverse: false,
      }}
      axisTop={null}
      axisRight={null}
      axisBottom={{
        orient: 'bottom',
        tickSize: 5,
        tickPadding: 5,
        tickRotation: 0,
        legend: 'Month',
        legendOffset: 36,
        legendPosition: 'middle',
      }}
      axisLeft={{
        orient: 'left',
        tickSize: 5,
        tickPadding: 5,
        tickRotation: 0,
        legend: 'Value',
        legendOffset: -40,
        legendPosition: 'middle',
      }}
      colors={{ scheme: 'nivo' }}
      pointSize={10}
      pointColor={{ theme: 'background' }}
      pointBorderWidth={2}
      pointBorderColor={{ from: 'serieColor' }}
      pointLabelYOffset={-12}
      useMesh={true}
      legends={[
        {
          anchor: 'bottom-right',
          direction: 'column',
          translateX: 100,
          itemWidth: 80,
          itemHeight: 20,
          symbolSize: 12,
          symbolShape: 'circle',
          effects: [
            {
              on: 'hover',
              style: {
                itemBackground: 'rgba(0, 0, 0, .03)',
                itemOpacity: 1,
              },
            },
          ],
        },
      ]}
    />
  </div>
  );
};

export default LineChart;
