import React from 'react';
import { ResponsiveRadar } from '@nivo/radar';

const data = [
  { taste: 'fruity', chardonnay: 80, cabernet: 65, syrah: 70 },
  { taste: 'bitter', chardonnay: 60, cabernet: 80, syrah: 50 },
  { taste: 'heavy', chardonnay: 70, cabernet: 55, syrah: 80 },
  { taste: 'strong', chardonnay: 80, cabernet: 70, syrah: 75 },
];

const RadarChart = () => (
  <div style={{ height: 400 }}>
    <ResponsiveRadar
      data={data}
      keys={['chardonnay', 'cabernet', 'syrah']}
      indexBy="taste"
      maxValue="auto"
      margin={{ top: 70, right: 80, bottom: 40, left: 80 }}
      curve="linearClosed"
      borderWidth={2}
      borderColor={{ from: 'color' }}
      gridShape="linear"
      gridLabelOffset={36}
      enableDots={true}
      dotSize={10}
      dotColor={{ theme: 'background' }}
      dotBorderWidth={2}
      dotBorderColor={{ from: 'color' }}
      colors={{ scheme: 'nivo' }}
      fillOpacity={0.25}
      animate={true}
      motionConfig="wobbly"
    />
  </div>
);

export default RadarChart;
