// src/components/CalendarChart.jsx
import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import { ResponsiveCalendar } from '@nivo/calendar';

const CalendarChart = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  fetch('/data/Total_co2.csv')
      .then(response => response.text())
      .then(csvText => {
        Papa.parse(csvText, {
          header: true,
          dynamicTyping: true,
          complete: (result) => {
            console.log(result);
            const formattedData = result.data.map(item => ({
              value: item["Total (kkg CO2e)"],
              day: `${item["Year Built"]}-01-01`
            }));            
            setData(formattedData);
            console.log(formattedData);
            setLoading(false);
          }
        });
      })
      .catch(error => {
        console.error('Error loading campus.csv:', error);
        setLoading(false);
      });

  if (loading) return <div>Loading data...</div>;

  console.log(data);

  return (
    <div style={{ height: 400 }}>
      <ResponsiveCalendar
        data={data}
        from={data[0]?.day || "2020-01-01"}
        to={data[data.length - 1]?.day || "2024-12-31"}
        emptyColor="#eeeeee"
        colors={["#61cdbb", "#97e3d5", "#e8c1a0", "#f47560"]}
        margin={{ top: 40, right: 40, bottom: 40, left: 40 }}
        yearSpacing={40}
        monthBorderColor="#ffffff"
        dayBorderWidth={2}
        dayBorderColor="#ffffff"
      />
    </div>
  );
};

export default CalendarChart;
