// src/components/CalendarChart.jsx
import React, { useMemo } from 'react';
import { ResponsiveCalendar } from '@nivo/calendar';
import { useData } from '../context/DataProvider';

const CalendarChart = () => {
  const { campusData, loading } = useData();

  const calendarData = useMemo(() => {
    if (!campusData || !campusData.data || !campusData.meta) return [];
    const formattedData = campusData.data.map(item => ({
      value: item["Total (kkg CO2e)"],
      day: `${item["Year Built"]}-01-01`
    }));    
    return formattedData;
  }, [campusData]);

  if (loading) return <div>Loading data...</div>;
  if (!calendarData.length) return <div>No data available</div>;

  return (
    <div style={{ height: 400 }}>
      <ResponsiveCalendar
        data={calendarData}
        from={calendarData[0]?.day || '2020-01-01'}
        to={calendarData[calendarData.length - 1]?.day || '2024-12-31'}
        emptyColor="#eeeeee"
        colors={['#61cdbb', '#97e3d5', '#e8c1a0', '#f47560']}
        margin={{ top: 40, right: 40, bottom: 40, left: 40 }}
        yearSpacing={40}
        monthBorderColor="#ffffff"
        dayBorderWidth={2}
        dayBorderColor="#ffffff"
      />
    </div>
  );
};

export default CalendarChart;
