import React, { useMemo } from 'react';
import { ResponsiveNetwork } from '@nivo/network'
import { useData } from '../context/DataProvider';

const LineChart = () => {
  const { totalCO2Data, loading } = useData();

  const calendarData = useMemo(() => {
    console.log(totalCO2Data);
    if (!totalCO2Data || !totalCO2Data.data || !totalCO2Data.meta) return null;

    function getRandomColorHSL() {
      return `hsl(${Math.floor(Math.random() * 360)}, 70%, 50%)`;
    }

    const nodes = [];
    const links = [];
    const seenNodes = new Set();

    totalCO2Data.data.forEach(item => {
      const type = item["UMD Type"];
      const building = item["Building Name"];
      const value = item["Total (kkg CO2e)"];

      if (!type || !building || typeof value !== 'number') return;

      // Add department node if not already seen
      if (!seenNodes.has(type)) {
        nodes.push({
          id: type,
          height: 1,
          size: 20,
          color: getRandomColorHSL(),
        });
        seenNodes.add(type);
      }

      // Add building node
      const buildingId = `${type}-${building}`;
      nodes.push({
        id: buildingId,
        height: 0,
        size: Math.max(value / 1000, 5), // size scaled
        color: getRandomColorHSL(),
      });

      // Create link from department → building
      links.push({
        source: type,
        target: buildingId,
        distance: 30,
      });
    });

    return { nodes, links };
  }, [totalCO2Data]);  

  if (loading) return <div>Loading data...</div>;
  if (!calendarData) return <div>No data available</div>;  

  console.log(calendarData)

  const data = [
    {
      id: 'Series 1',
      data: [
        { x: 'Jan', y: 200 },
        { x: 'Feb', y: 300 },
        { x: 'Mar', y: 400 },
        { x: 'Apr', y: 500 },
        { x: 'May', y: 600 },
      ],
    },
    {
      id: 'Series 2',
      data: [
        { x: 'Jan', y: 100 },
        { x: 'Feb', y: 400 },
        { x: 'Mar', y: 250 },
        { x: 'Apr', y: 450 },
        { x: 'May', y: 300 },
      ],
    },
  ];

  return (
    <div style={{ height: 400 }}>
    <ResponsiveNetwork
        data={calendarData}
        margin={{ top: 0, right: 0, bottom: 0, left: 0 }}
        linkDistance={e=>e.distance}
        centeringStrength={0.3}
        repulsivity={6}
        nodeSize={n=>n.size}
        activeNodeSize={n=>1.5*n.size}
        nodeColor={e=>e.color}
        nodeBorderWidth={1}
        nodeBorderColor={{
            from: 'color',
            modifiers: [
                [
                    'darker',
                    0.8
                ]
            ]
        }}
        linkThickness={n=>2+2*n.target.data.height}
        linkBlendMode="multiply"
        motionConfig="wobbly"
    />
  </div>
  );
};

export default LineChart;
