import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';
import img1 from './assets/img_1_philly.jpg';
import img2 from './assets/img_2_philly.jpg';

function App() {
  const [fade, setFade] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const vh = window.innerHeight;
      // map scroll [0, vh] → fade [0,1]
      const fraction = Math.min(Math.max(scrollY / vh, 0), 1);
      setFade(fraction);
    };
    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="App">
      <div className="bg-container">
        <div
          className="bg-image"
          style={{ backgroundImage: `url(${img1})`, opacity: 1 - fade }}
        />
        <div
          className="bg-image"
          style={{ backgroundImage: `url(${img2})`, opacity: fade }}
        />
        <div className="overlay">
          {fade < 0.5 ? (
            <div className="text-block">
              <h1>IsoPhilly</h1>
              <p>Your go‑to immersive Philly experience</p>
            </div>
          ) : (
            <div className="text-block">
              <p>
                Explore the hidden gems of the City of Brotherly Love with our interactive
                map below.
              </p>
            </div>
          )}
        </div>
      </div>

      <div className="map-container">
        <MapContainer
          center={[39.9526, -75.1652]}
          zoom={13}
          style={{ height: '100%', width: '100%' }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
        </MapContainer>
      </div>
    </div>
  );
}

export default App;
