import React, { useMemo } from 'react';
import { ResponsiveSunburst } from '@nivo/sunburst'
import { useData } from '../context/DataProvider';

const PieChart = () => {
  const { totalCO2Data, loading } = useData();

  const calendarData = useMemo(() => {
    console.log(totalCO2Data);
    if (!totalCO2Data || !totalCO2Data.data || !totalCO2Data.meta) return null;

    function getRandomColorHSL() {
      return `hsl(${Math.floor(Math.random() * 360)}, 70%, 50%)`;
    }

    const groupMap = {};

    totalCO2Data.data.forEach(item => {
      const type = item["UMD Type"];
      const name = item["Building Name"];
      const value = item["Total (kkg CO2e)"];

      if (!type || !name || typeof value !== "number") return;

      if (!groupMap[type]) {
        groupMap[type] = {
          name: type,
          color: getRandomColorHSL(),
          children: [],
        };
      }

      groupMap[type].children.push({
        name: name,
        color: getRandomColorHSL(),
        loc: value,
      });
    });

    const sunburstData = {
      name: "UMD Buildings",
      color: getRandomColorHSL(),
      children: Object.values(groupMap),
    };

    console.log(sunburstData);
    return sunburstData;
  }, [totalCO2Data]);  

  if (loading) return <div>Loading data...</div>;
  if (!calendarData) return <div>No data available</div>;  

  console.log(calendarData)

  const data = [
    {
      id: 'Series 1',
      data: [
        { x: 'Jan', y: 200 },
        { x: 'Feb', y: 300 },
        { x: 'Mar', y: 400 },
        { x: 'Apr', y: 500 },
        { x: 'May', y: 600 },
      ],
    },
    {
      id: 'Series 2',
      data: [
        { x: 'Jan', y: 100 },
        { x: 'Feb', y: 400 },
        { x: 'Mar', y: 250 },
        { x: 'Apr', y: 450 },
        { x: 'May', y: 300 },
      ],
    },
  ];

  return (
    <div style={{ height: 800 }}>
    <ResponsiveSunburst
        data={data}
        margin={{ top: 10, right: 10, bottom: 10, left: 10 }}
        id="name"
        value="loc"
        cornerRadius={2}
        borderColor={{ theme: 'background' }}
        colors={{ scheme: 'nivo' }}
        childColor={{
            from: 'color',
            modifiers: [
                [
                    'brighter',
                    0.1
                ]
            ]
        }}
        enableArcLabels={true}
        arcLabelsSkipAngle={10}
        arcLabelsTextColor={{
            from: 'color',
            modifiers: [
                [
                    'darker',
                    1.4
                ]
            ]
        }}
    />
  </div>
  );
};

export default PieChart;
