import React, { useMemo } from 'react';
import { ResponsiveSunburst } from '@nivo/sunburst'
import { useData } from '../context/DataProvider';

const RadarChart = () => {
  const { totalCO2Data, loading } = useData();

  const calendarData = useMemo(() => {
    if (!totalCO2Data || !totalCO2Data.data || !totalCO2Data.meta) return null;

    const groupMap = {};

    totalCO2Data.data.forEach(item => {
      const type = item["UMD Type"];
      const name = item["Building Name"];
      const value = item["Total (kkg CO2e)"];

      if (!type || !name || typeof value !== "number") return;

      if (!groupMap[type]) {
        groupMap[type] = {
          name: type,
          children: []
        };
      }

      groupMap[type].children.push({
        name,
        value
      });
    });

    return {
      name: "UMD Buildings",
      children: Object.values(groupMap)
    };
  }, [totalCO2Data]);    

  if (loading) return <div>Loading data...</div>;
  if (!calendarData) return <div>No data available</div>;  

  console.log(calendarData)

  return (
  <div style={{ height: '800px' }}>
    <ResponsiveCirclePacking
        data={calendarData}
        margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
        id="name"
        value="value"
        colors={{ scheme: 'nivo' }}
        childColor={{
          from: 'color',
          modifiers: [['brighter', 0.4]]
        }}
        padding={4}
        enableLabels={true}
        labelsSkipRadius={10}
        labelTextColor={{
          from: 'color',
          modifiers: [['darker', 2]]
        }}
        borderWidth={1}
        borderColor={{
          from: 'color',
          modifiers: [['darker', 0.5]]
        }}
      />
    </div>
  );
};

export default RadarChart;
